$filepath = "D:\_temp\" # pre-programatically
#$filepath = read-host "Enter the directory" # Get filepath from user input. Doesn't create the folder though. You have to create it first with the "\" character at the end.

$filename = "File_00" # file name
$fileext = ".log" # file extension
$filenum = read-host "How many files do you want to create?" # prompt user for number of files to create
$i = $null #resets the counter for number of files

for ($i=0;$i-le $filenum;$i++){
    Set-content -Path $filepath$filename$i$fileext -Value $textvalue
}
Write-host $i files created in $filepath

